    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        
        String up = userPassword.getText();
        String cp = userConfirmPassword.getText();
        if(up.equals(cp))
           
               {
                String user_name = userName.getText();
                String user_password = userPassword.getText();
                String user_firstname = userFirstName.getText();
                String user_lastname = userLastName.getText();
                // creating one object 
                ResumeUser obj = new ResumeUser();
                obj.create_user(user_name, user_password, user_firstname, user_lastname);
      
        
                LoginScreen LS = new LoginScreen();
                LS.show();
                dispose();
           
           }
        else 
        {NotMatched NM = new NotMatched();
           NM.show();}    // TODO add your handling code here:
    }//GEN-LAST:event_jButton2ActionPerformed

